mapper.select:
SELECT CardID, Name, RC_NO, SwipeCardTime, SwipeCardTime2 FROM testSwipeCardTime
WHERE WorkshopNo = #{WorkshopNo}
AND swipecardtime >curdate()

SELECT count(*) 
FROM `testSwipeCardTime`
WHERE ID = #{Id}

  AND SwipeCardTime is not NULL
  AND Shift = #{Shift}
  AND  SwipeCardTime > curdate() and SwipeCardTime < adddate(curdate(), '+1 days')

select count(*) from `testSwipeCardTime` where id = #{Id} 

AND SwipeCardTime2 is not NULL 
and  SwipeCardTime > curdate() and SwipeCardTime < adddate(curdate(), '+1 days')

select count(*) from `testSwipeCardTime` where ID = #{id} 

AND Shift = #{Shift}
AND SwipeCardTime2 is not NULL 
and  SwipeCardTime > date_sub(curdate(),interval 1 day) and SwipeCardTime < curdate()

select count(*) from `testSwipeCardTime` where ID = #{Id} 

AND Shift = #{Shift}
AND SwipeCardTime is not NULL 
and  SwipeCardTime > date_sub(curdate(),interval 1 day) and SwipeCardTime < curdate()

select count(*)  from `testSwipeCardTime` where ID = #{Id} 

AND Shift = #{Shift}
AND SwipeCardTime is NULL and SwipeCardTime2 is not null
and  SwipeCardTime2 > curdate() and SwipeCardTime2 < adddate(curdate(), '+1 days')

mapper.update:
    	update testSwipeCardTime set SwipeCardTime2 = #{SwipeCardTime2},CardID=#{CardID} 
    	WHERE ID = #{Id} 
    	and Shift = #{Shift} 

		and  date_format(SwipeCardTime,'%Y-%m-%d') = curdate()

    	update testSwipeCardTime set SwipeCardTime2 = #{SwipeCardTime2},CardID=#{CardID} 
    	WHERE ID = #{Id} 
    	and Shift = #{Shift} 

		and  date_format(SwipeCardTime,'%Y-%m-%d') = date_format(date_sub(#{SwipeCardTime2},interval 24 hour),'%Y-%m-%d')

    	update testSwipeCardTime set SwipeCardTime2 = #{SwipeCardTime2} 
    	WHERE CardID = #{CardID} and WorkshopNo=#{WorkshopNo}
		  and  SwipeCardTime > curdate()

    	update testSwipeCardTime set SwipeCardTime2 = #{SwipeCardTime2} 
    	WHERE CardID = #{CardID} and Shift = #{Shift}
			and  date_format(SwipeCardTime,'%Y-%m-%d') = curdate()

    	update testSwipeCardTime set SwipeCardTime2 = #{SwipeCardTime2} 
    	WHERE CardID = #{CardID}
    		and Shift = #{Shift}
			and  date_format(SwipeCardTime,'%Y-%m-%d') = date_format(date_sub(#{SwipeCardTime2},interval 12 hour),'%Y-%m-%d')

